import React from "react";
import styled from "styled-components";

const options = [
  "sukiicat",
  "albertbabycat",
  "smoothiethecat",
  "realgrumpycat"
];

const SearchContainer = styled.div`
  margin-top: 56px;
`;

export const Search = ({ search, onSearch }) => {
  return (
    <SearchContainer>
      {options.map(option => (
        <label key={option}>
          <input
            type="radio"
            value={option}
            checked={option === search}
            onChange={event => onSearch(event.target.value)}
          />
          {option}
        </label>
      ))}
    </SearchContainer>
  );
};
