import React from "react";
import styled from "styled-components";

const PetsErrorContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  height: 100%;
  width: 100%;
`;

const ErrorImg = styled.img`
  width: 100%;
`;

const ErrorMessage = styled.div`
  text-align: center;
  font-weight: bold;
`;

export const PetsError = ({ error }) => {
  const { status, message } = error;

  return (
    <PetsErrorContainer>
      <ErrorImg src="./serverError.jpeg" alt="request error" />
      <ErrorMessage>
        <div>{status}</div>
        <div>{message}</div>
      </ErrorMessage>
    </PetsErrorContainer>
  );
};
