import React from "react";
import styled from "styled-components";

const PetContainer = styled.article`
  border: 1px solid ${({ theme }) => theme.colors.tertiary};
  border-radius: 3px;
  margin-bottom: 56px;
`;

const PetHeader = styled.header`
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 16px;
`;

const PetNameContainer = styled.div`
  display: flex;
  flex-grow: 1;
  flex-direction: column;
  justify-content: center;
  margin-left: 16px;
`;

const PetName = styled.div`
  font-family: Arial, Helvetica, sans-serif;
  font-weight: bold;
`;

const PetProfileIMageBorder = styled.span`
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  height: 40px;
  width: 40px;
  border-radius: 100%;
  background: #d6249f;
  background: radial-gradient(
    circle at 30% 107%,
    #fdf497 0%,
    #fdf497 5%,
    #fd5949 45%,
    #d6249f 60%,
    #285aeb 90%
  );
`;

const PetProfileImage = styled.img`
  height: 32px;
  width: 32px;
  border-radius: 100%;
  border: solid 2px white;
`;

const PetBody = styled.div`
  min-height: 300px;
  will-change: height;
  transition: height 0.3s;
`;

const PetImage = styled.img`
  width: 100%;
  display: flex;
`;

export const PetCard = ({ pet }) => {
  return (
    <PetContainer>
      <PetHeader>
        <PetProfileIMageBorder>
          <PetProfileImage src={pet.profileImage} alt={pet.name} />
        </PetProfileIMageBorder>
        <PetNameContainer>
          <PetName>{pet.name}</PetName>
        </PetNameContainer>
      </PetHeader>
      <PetBody>
        <PetImage src={pet.photo} />
      </PetBody>
    </PetContainer>
  );
};
