import React, { Component } from "react";

export class PetsError extends Component {
  state = {
    error: null
  };

  componentDidCatch = (error, errorInfo) => {
    this.setState({ error });
    console.log({ error, errorInfo });
  };

  render = () => {
    if (this.state.error) return <div>Error</div>;

    return this.props.children;
  };
}
