import React from "react";
import ReactDOM from "react-dom";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import styled, { ThemeProvider, createGlobalStyle } from "styled-components";

import { SuspenseFetch } from "./modules/SuspenseFetch";
import { SuspenseFetchError } from "./modules/SuspenseFetchError";
import { NormalFetch } from "./modules/NormalFetch";
import { NormalFetchError } from "./modules/NormalFetchError";

import { Nav } from "./shared/Nav";
import { Header } from "./shared/Header";

const theme = {
  skeleton: `
    background: #ebebeb;
    position: relative;
    overflow: hidden;

    &::after {
      display: block;
      content: "";
      position: absolute;
      width: 100%;
      height: 100%;
      transform: translateX(-100%);
      background: linear-gradient(
        90deg,
        transparent,
        rgba(255, 255, 255, 0.4),
        transparent
      );
      animation: loading 1.5s infinite;
    }

    @keyframes loading {
      100% {
        transform: translateX(100%);
      }
    }
  `,
  colors: {
    primary: "",
    secondary: "",
    tertiary: "#dbdbdb",
    cuaternary: "white"
  }
};

const rootElement = document.getElementById("root");

const GlobalStyles = createGlobalStyle`
  body {
    margin: 0px;
    padding: 0px;
    font-family: Arial;
  }
`;

const AppMain = styled.main`
  margin-top: 56px;
  overflow: auto;
  height: calc(100% - 56px);
`;

const AppContainer = styled.div`
  display: flex;
`;

const Root = () => (
  <ThemeProvider theme={theme}>
    <GlobalStyles />
    <BrowserRouter>
      <AppContainer>
        <Nav />
        <Header />

        <AppMain>
          <Switch>
            <Route path="/normal-fetch-data">
              <NormalFetch />
            </Route>
            <Route path="/normal-fetch-error">
              <NormalFetchError />
            </Route>
            <Route path="/suspense-fetch-data">
              <SuspenseFetch />
            </Route>
            <Route path="/suspense-fetch-error">
              <SuspenseFetchError />
            </Route>
          </Switch>
        </AppMain>
      </AppContainer>
    </BrowserRouter>
  </ThemeProvider>
);

ReactDOM.render(<Root />, rootElement);
