import React from "react";
import styled from "styled-components";

const options = [
  "sukiicat",
  "albertbabycat",
  "smoothiethecat",
  "realgrumpycat"
];

const SearchContainer = styled.div`
  margin-top: 56px;
  display: flex;
`;

const SearchLabel = styled.label`
  flex: 1;
  user-select: none;
  border: solid 1px ${({ theme }) => theme.colors.tertiary};
  padding: 8px;
  font-size: 12px;
  font-weight: bold;
  text-align: center;

  &:not(:last-child) {
    margin-right: 8px;
  }
`;

const SearchInput = styled.input`
  display: none;
`;

export const Search = ({ search, onSearch }) => {
  return (
    <SearchContainer>
      {options.map(option => (
        <SearchLabel key={option}>
          <SearchInput
            type="radio"
            value={option}
            checked={option === search}
            onChange={event => onSearch(event.target.value)}
          />
          {option}
        </SearchLabel>
      ))}
    </SearchContainer>
  );
};
